#!/usr/bin/env python3# -*- coding: utf-8 -*-"""Created on Sat May  2 22:05:24 2020@author: fassaad"""import osimport subprocessimport shutilimport sysfrom colorama import Fore, Back, Styleclass Variable:    def __init__(self, namespace, name, value):        self.namespace = namespace        self.name  = name        self.value = valuedef Set_Default_Variables():    Params = [Variable("VAR_Lattice","L1",6),Variable("VAR_Lattice","L2",6)]    Params = Params + [Variable("VAR_Lattice","Lattice_type", "Square")  ]    Params = Params + [Variable("VAR_Lattice","Model"       , "Hubbard") ]    #    Params = Params + [Variable("VAR_Model_Generic","Checkerboard", ".T.")]    Params = Params + [Variable("VAR_Model_Generic","Symm"        , ".T.")]    Params = Params + [Variable("VAR_Model_Generic","N_SUN"       , "2"  )]    Params = Params + [Variable("VAR_Model_Generic","N_FL"        , "1"  )]    Params = Params + [Variable("VAR_Model_Generic","Phi_X"       , "0.0")]    Params = Params + [Variable("VAR_Model_Generic","Phi_Y"       , "0.0")]    Params = Params + [Variable("VAR_Model_Generic","Bulk"        , ".T.")]    Params = Params + [Variable("VAR_Model_Generic","N_Phi"       , "0"  )]    Params = Params + [Variable("VAR_Model_Generic","Dtau"        , "0.1")]    Params = Params + [Variable("VAR_Model_Generic","Beta"        , "5.0")]    Params = Params + [Variable("VAR_Model_Generic","Projector"   , ".F.")]    Params = Params + [Variable("VAR_Model_Generic","Theta"       , "10.0")]    #    Params = Params + [Variable("VAR_Hubbard" ,"HS"       ,  "Mz"   )]    Params = Params + [Variable("VAR_Hubbard" ,"ham_T"    ,  "1.0"  )]    Params = Params + [Variable("VAR_Hubbard" ,"ham_chem" ,  "0.0"  )]    Params = Params + [Variable("VAR_Hubbard" ,"ham_U"    ,  "4.0"  )]    Params = Params + [Variable("VAR_Hubbard" ,"ham_T2"   ,  "1.0"  )]    Params = Params + [Variable("VAR_Hubbard" ,"ham_U2"   ,  "4.0"  )]    Params = Params + [Variable("VAR_Hubbard" ,"ham_Tperp",  "1.0"  )]    #    Params = Params + [Variable("VAR_QMC","Nwrap"              , "10" )]    Params = Params + [Variable("VAR_QMC","NSweep"             , "20" )]    Params = Params + [Variable("VAR_QMC","NBin"               , "5"  )]    Params = Params + [Variable("VAR_QMC","Ltau"               , "1"  )]    Params = Params + [Variable("VAR_QMC","LOBS_ST"            , "0"  )]    Params = Params + [Variable("VAR_QMC","LOBS_EN"            , "0"  )]    Params = Params + [Variable("VAR_QMC","CPU_MAX"            , "0.0")]    Params = Params + [Variable("VAR_QMC","Propose_S0"         , ".F.")]    Params = Params + [Variable("VAR_QMC","Global_moves"       , ".F.")]    Params = Params + [Variable("VAR_QMC","N_Global"           , "1"  )]    Params = Params + [Variable("VAR_QMC","Global_tau_moves"   , ".F.")]    Params = Params + [Variable("VAR_QMC","N_Global_tau"       , "1"  )]    Params = Params + [Variable("VAR_QMC","Nt_sequential_start", "0"  )]    Params = Params + [Variable("VAR_QMC","Nt_sequential_end"  , "-1" )]    #    Params = Params + [Variable("VAR_errors","n_skip" , "1")]    Params = Params + [Variable("VAR_errors","N_rebin", "1")]    Params = Params + [Variable("VAR_errors","N_Cov"  , "0")]    #    Params = Params + [Variable("VAR_TEMP","N_exchange_steps"      , "6"   )]    Params = Params + [Variable("VAR_TEMP","N_Tempering_frequency" , "10"  )]    Params = Params + [Variable("VAR_TEMP","mpi_per_parameter_set" , "2"   )]    Params = Params + [Variable("VAR_TEMP","Tempering_calc_det"    , ".T." )]    #    Params = Params + [Variable("VAR_Max_Stoch", "NGamma"     , "400"  )]    Params = Params + [Variable("VAR_Max_Stoch", "Om_st"      , "-10.0")]    Params = Params + [Variable("VAR_Max_Stoch", "Om_en"      , " 10.0")]    Params = Params + [Variable("VAR_Max_Stoch", "Ndis"       , "2000" )]    Params = Params + [Variable("VAR_Max_Stoch", "NBins"      , "250"  )]    Params = Params + [Variable("VAR_Max_Stoch", "NSweeps"    , "70"   )]    Params = Params + [Variable("VAR_Max_Stoch", "NWarm"      , "20"   )]    Params = Params + [Variable("VAR_Max_Stoch", "N_alpha"    , "14"   )]    Params = Params + [Variable("VAR_Max_Stoch", "alpha_st"   , "1.d0" )]    Params = Params + [Variable("VAR_Max_Stoch", "R"          , "1.2"  )]    Params = Params + [Variable("VAR_Max_Stoch", "Channel"    , "P"    )]    Params = Params + [Variable("VAR_Max_Stoch", "Checkpoint" , ".F."  )]    Params = Params + [Variable("VAR_Max_Stoch", "Tolerance"  , "0.1"  )]    #    return Paramsdef Print_parameters(Params,model,file):    print ("Setting up parameter file for", model, file )    if os.path.exists(file):        os.remove(file)    f=open(file,"a")    VAR_model ="VAR_" + str(model).strip()    namespaces =[ "VAR_Lattice", "VAR_Model_Generic" , VAR_model, "VAR_QMC",                 "VAR_errors", "VAR_Max_Stoch", "VAR_TEMP" ]    for  name in namespaces:        f.write("&"+str(name).strip())        f.write("\n")        for Var in Params:            if Var.namespace  ==  name:                if Var.name in ["Lattice_type","Model", "HS", "Channel" ]:                    f.write(str(Var.name+" = "+"\""+Var.value+"\""))                else:                    f.write(str(Var.name+" = "+Var.value))                f.write("\n")        f.write("/")        f.write("\n")        f.write("\n")    f.closedef Directory_name(Sim):    Dir=str()    for name in Sim.split(','):        if name.split('=')[0].strip() in ["L1", "L2","Lattice_type","Model",                    "Checkerboard","Symm","N_SUN","N_FL", "Phi_X","N_Phi",                    "Dtau","Beta","Projector",                    "Theta", "ham_T","ham_chem","ham_U",                    "ham_T2", "ham_U2", "ham_Tperp"]:            if name.split('=')[0].strip() in ["Lattice_type","Model"]:                Dir=Dir+str(name.split('=')[1].strip()+"_")            else:                Dir=Dir+str(name.split('=')[0].strip().strip("ham_")+"="+                            name.split('=')[1].strip()+"_")    return Dir[:-1]def Check_Var(name):    #print("check",name)    if str(name).strip() not in   ["L1", "L2", "Lattice_type", "Model",                  "Checkerboard", "Symm",                  "N_SUN", "N_FL","Phi_X", "Phi_Y", "Bulk", "N_Phi", "Dtau",                  "Beta", "Projector", "Theta", "HS","ham_T", "ham_chem",                  "ham_U", "ham_T2", "ham_U2", "ham_Tperp", "Nwrap",                  "NSweep", "NBin", "Ltau", "LOBS_ST",                  "LOBS_EN", "CPU_MAX", "Propose_S0", "Global_moves",                  "N_Global", "Global_tau_moves","N_Global_tau",                  "Nt_sequential_start", "Nt_sequential_end", "n_skip",                  "N_rebin", "N_Cov", "NGamma", "Om_st", "Om_en", "Ndis",                  "NBins", "NSweeps", "NWarm", "N_alpha","alpha_st", "R",                  "Channel", "Checkpoint", "Tolerance", "N_exchange_steps",                  "N_Tempering_frequency", "mpi_per_parameter_set",                  "Tempering_calc_det"]:        print("Name ",name, " does not correspond to a parameter" )        exit()def Set_param(Sim):    if str(Sim.split(',')[0].split('=')[0].strip()).lower() ==  "stop":        print("Done")        exit()    Params = Set_Default_Variables()    for Var_sim in Sim.split(','):        Check_Var(str(Var_sim.split('=')[0]).strip())        for Var in Params:            if  str(Var_sim.split('=')[0]).strip() == str(Var.name).strip():                Var.value = str(Var_sim.split('=')[1]).strip()    return Paramsdef Run(rundir,Alfdir,Runbranch,Config,Executable,Params):    if os.path.exists(rundir):        os.chdir(rundir)        os.system("rm parameters")        os.system("bash out_to_in.sh")    else:        os.mkdir(rundir)        os.chdir(rundir)        os.system("cp ../Start/* .")    Print_parameters(Params,model,"parameters")    os.chdir(Alfdir)    os.system("make clean")    command=str("git checkout " + Runbranch )     print (Fore.RED+command)    print(Style.RESET_ALL)    os.system(command)    command=str(". ./configureHPC.sh " + Config + "; make " + Executable )    print (Fore.RED+command)    print(Style.RESET_ALL)    os.system(command)    os.chdir(rundir)    command=str(Alfdir+"/Prog/" + str(Executable).strip() + ".out" )    print (Fore.RED+command)    print(Style.RESET_ALL)    os.system(command)    os.chdir(Alfdir)    command=str(". ./configureHPC.sh " + Config + "; make ana " )    print (Fore.RED+command)    print(Style.RESET_ALL)    os.system(command)    os.chdir(rundir)    command=str( "cd " + Alfdir+ "; . ./configureHPC.sh " + Config + "; cd "                    + rundir + "; bash analysis.sh" )    print (Fore.RED+command)    os.system(command)if __name__ == "__main__":    print ('Number of arguments:', len(sys.argv), 'arguments.' )    print (sys.argv)    if len(sys.argv) == 1:        print(Fore.RED+"Usage: python3 Run.py Type=  Alfdir=  Runbranch=  Program= Testbranch= Config=")        print(Style.RESET_ALL)        print("Type        : R+T, R, T        with R=Run,  T=Test. Default=R+A")        print("Alfdir      : Path to ALF directory.                Mandatory")        print("branch_R    : Will run git checkout \"Runbranch\".    Default=master")        print("Executable_R: Name of ref executable                Default=\"Model\".out")        print("Config      : Will run ./configureHPC \"Config\"      Default=Intel")        print("branch_T    : Branch to test against Runbranch      Default=master")        print("Executable_T: Name of test executable               Default=\"Model\".out")    # Default    Type         = "R"    Alfdir       = "none"    branch_R     = "master"    branch_T     = "master"    Config       = "Intel"    Executable_R = "none"    Executable_T = "none"    print(sys.argv)    for arg in sys.argv:        if arg.split('=')[0].lower() == "type" :            Type=str(arg.split('=')[1])        if arg.split('=')[0].lower() == "alfdir" :            Alfdir=str(arg.split('=')[1])        if arg.split('=')[0].lower() == "branch_r" :            branch_R=str(arg.split('=')[1])        if arg.split('=')[0].lower() == "config" :            Config=str(arg.split('=')[1])        if arg.split('=')[0].lower() == "branch_t" :            branch_T=str(arg.split('=')[1])        if arg.split('=')[0].lower() == "executable_r" :            Executable_R=str(arg.split('=')[1])        if arg.split('=')[0].lower() == "executable_t" :            Executable_T=str(arg.split('=')[1])    if  Alfdir == "none" :        print("Alfdir is mandatory")        exit()    f = open("Sims")    Simulations = f.read().splitlines()    f.close()    print ( "Number of simulations ", len(Simulations))    for Sim in Simulations:        Params= Set_param(Sim)        model = Sim.split(',')[0].split('=')[1].strip()        print("Model is", model)         Dir = Directory_name(Sim)        print(Dir)        cwd = os.getcwd()        rundir = str(cwd+"/"+Dir)        print ("rundir is ", rundir)        print(Type, Type.split("+"),Type.split("+")[0] )        if "R" in str(Type.split("+")).strip():            if Executable_R == "none":                Executable1 = model            else:                Executable1 = Executable_R            Run(rundir,Alfdir,branch_R,Config,Executable1,Params)            f=open("Kin_scalJ")            Kin_R=f.read().splitlines()[2]            f.close                       os.chdir(cwd)        if "T" in str(Type.split("+")).strip():            rundirT=str(rundir+"_Test")            if Executable_T == "none":                Executable1 = model            else:                Executable1 = Executable_T            Run(rundirT,Alfdir,branch_T,Config,Executable1,Params)            f=open("Kin_scalJ")            Kin_T=f.read().splitlines()[2]            f.close         os.chdir(cwd)        f= open(str(rundir)+".txt","w")        f.write(Kin_T)        f.write(Kin_R)        f.close                # You need to write a general  run routine #  in ALFdir, Branch,  Config, rundir,  Executable# You need to write ageneral analysis routine#  in ALFdir, Branch,  Config, rundir#    directory = '/Users/fassaad'#    os.chdir(directory)#    cwd = os.getcwd()#    print ("Current working directory is:", cwd)#    for filename in os.listdir(directory):#        print(filename)#        if filename.endswith(".txt")#            f = open(filename)#            lines = f.readlines()#            print (filename)#            print (lines[0])#            continue#        else:#            continue#directory = '/Users/fassaad'#os.chdir(directory)#cwd = os.getcwd()#s = open("TEST.txt").read()#replace = '20'#s = s.replace('L2_R', replace)#f = open("TEST.txt", 'w')#f.write(s)#f.close()#s = open("TEST.txt").read()#replace = '10'#s = s.replace('L1_R', replace)#f = open("TEST.txt", 'w')#f.write(s)#f.close()#####import subprocess#directory = '/Users/fassaad'#os.chdir(directory)#hi = 'hithere'#Command = ' export Hi="hi"  ; echo $Hi '#Tmp = subprocess.check_output( str(Command) , shell=True)#print(Tmp)